useEffect(() => {
  fetch('/news.json')
    .then(response => response.json())
    .then(data => {
      console.log(data);
      if (data && Array.isArray(data.articles)) {
        setArticles(data.articles);
      } else {
        setArticles([]);
      }
    })
    .catch(error => {
      console.error("API Error: ", error);
      setArticles([]);
    });
}, []);
