import React, { useState, useEffect } from 'react';

function News() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    fetch('https://newsdata.io/api/1/news?apikey=pub_47286399fc8b4330aff0fdef71518eea&country=pk&language=en&category=top')
      .then(response => response.json())
      .then(data => {
        console.log(data);
        if (data && Array.isArray(data.results)) {
          setArticles(data.results);
        } else {
          setArticles([]);
        }
      })
      .catch(error => {
        console.error("API Error: ", error);
        setArticles([]);
      });
  }, []);

  return (
    <div className="container my-4">
      <h2>ðŸ“° Pakistan Top News</h2>
      <div className="row">
        {Array.isArray(articles) && articles.length > 0 ? (
          articles.map((article, index) => (
            <div className="col-md-4" key={index}>
              <h4>{article.title}</h4>
              <p>{article.description}</p>
              {article.image_url && <img src={article.image_url} alt="news" width="200" />}
              <br />
              <a href={article.link} target="_blank" rel="noopener noreferrer">Read more</a>
              <hr />
            </div>
          ))
        ) : (
          <p>Loading news or no news found.</p>
        )}
      </div>
    </div>
  );
}

export default News;
