import React from 'react';

function Newsitem(props) {
  const { title, description, url, imageUrl } = props;

  return (
    <div className="card my-2">
      {/* Image */}
      {imageUrl ? (
        <img src={imageUrl} className="card-img-top" alt="news" />
      ) : (
        <img src="https://via.placeholder.com/300x180.png?text=No+Image" className="card-img-top" alt="no news available" />
      )}

      {/* Body */}
      <div className="card-body">
        <h5 className="card-title">{title ? title : "No Title Available"}</h5>
        <p className="card-text">{description ? description : "No Description Available"}</p>
        <a href={url} target="_blank" rel="noopener noreferrer" className="btn btn-primary">
          Read More
        </a>
      </div>
    </div>
  );
}

export default Newsitem;
